<template >		<h1 style="margin-bottom: 15px">Страница с постами</h1>		<div class="app_btns">				<my-app-button class="primary"  @click="showDialog">Создать пост</my-app-button>				<my-app-select :modelValue="selectedSort" @update:modelValue="selectedSort = $event" :options="sortOptions"></my-app-select>		</div>				<my-app-dialog v-model:show="dialogVisible">				<post-form @create="createPost" />		</my-app-dialog>				<post-list				v-model="findTitle"				:posts="filteredPosts"				:onLoading="onLoading"				@update:modelValue="filterPosts"				@remove-post="removePost"				@done-post="donePost"		/></template ><script setup >import {computed, onMounted, ref, watch} from 'vue';import PostList from "./components/PostList.vue";import PostForm from './components/PostForm.vue';import axios from "axios";const posts = ref([])const findTitle = ref('');const dialogVisible = ref(false);const onLoading = ref(true)const selectedSort = ref('')const sortOptions = ref([		{value: 'title', name: 'По названию'},		{value: 'body', name: 'По содержимому'}])const createPost = (post) => {		posts.value.push({id: posts.value.length + 1, ...post});		dialogVisible.value = false}const filteredPosts = computed(() => {		return posts.value.filter(post =>				post.title.toLowerCase().includes(findTitle.value.toLowerCase())		);});function filterPosts (searchTerm) {		findTitle.value = searchTerm;}function removePost (id) {		posts.value = posts.value.filter(post => post.id !== id)}function donePost (id) {		posts.value = posts.value.map(post => {				if (post.id === id) {						post.done = !post.done				}				return post		})}function showDialog() {		dialogVisible.value = true}async function fetchPosts() {		try{				onLoading.value = true				const response = await axios.get('https://jsonplaceholder.typicode.com/posts?_limit=10' )				posts.value = response.data;		}		catch (e) {		alert(`Ошибка`)		}		finally {				onLoading.value = false		}}onMounted( async ()=>{	 	setTimeout(()=>fetchPosts(),		1500)		})// const sortedPosts = computed(() => {// 		return [...posts.value].sort((post1, post2) => {// 				return post1[selectedSort.value]?.localeCompare(post2[selectedSort.value])// 		})// })watch( selectedSort, (newValue) => {		posts.value.sort((post1, post2) => {				return post1[newValue]?.localeCompare(post2[newValue])		})})</script ><style ></style >