<template >		<post-form @create="createPost" />		<post-list				v-model="findTitle"				:posts="filteredPosts"				@update:modelValue="filterPosts"				@remove-post="removePost"				@done-post="donePost"		/></template ><script setup >import {computed, ref} from 'vue';import PostList from "./components/PostList.vue";import PostForm from './components/PostForm.vue';const posts = ref([		{id: 1, title: 'Javascript 1', body: 'JavaScript универсальный язык программирования 1', done: false},		{id: 2, title: 'Javascript 2', body: 'JavaScript универсальный язык программирования 2', done: false},		{id: 3, title: 'Javascript 3', body: 'JavaScript универсальный язык программирования 3', done: false}]);const findTitle = ref('');const createPost = (post) => {		posts.value.push({id: posts.value.length + 1, ...post});}const filteredPosts = computed(() => {		return posts.value.filter(post =>				post.title.toLowerCase().includes(findTitle.value.toLowerCase())		);});function filterPosts (searchTerm) {		findTitle.value = searchTerm;}function removePost (id) {		posts.value = posts.value.filter(post => post.id !== id)}function donePost (id) {		posts.value = posts.value.map(post => {				if (post.id === id) {						post.done = !post.done				}				return post		})}</script ><style ></style >