<template >		<h1 style="margin-bottom: 15px">Страница с постами</h1>		<my-app-button class="primary"  @click="showDialog">Создать пост</my-app-button>		<my-app-dialog v-model:show="dialogVisible">				<post-form @create="createPost" />		</my-app-dialog>		<post-list				v-model="findTitle"				:posts="filteredPosts"				@update:modelValue="filterPosts"				@remove-post="removePost"				@done-post="donePost"		/></template ><script setup >import {computed, ref} from 'vue';import PostList from "./components/PostList.vue";import PostForm from './components/PostForm.vue';import MyAppButton from "./components/ui/MyAppButton.vue";const posts = ref([		{id: 1, title: 'Javascript 1', body: 'JavaScript универсальный язык программирования 1', done: false},		{id: 2, title: 'Javascript 2', body: 'JavaScript универсальный язык программирования 2', done: false},		{id: 3, title: 'Javascript 3', body: 'JavaScript универсальный язык программирования 3', done: false},		{id: 4, title: 'Javascript 4', body: 'JavaScript универсальный язык программирования 4', done: false},		{id: 5, title: 'Javascript 5', body: 'JavaScript универсальный язык программирования 5', done: false},]);const findTitle = ref('');const dialogVisible = ref(false);const createPost = (post) => {		posts.value.push({id: posts.value.length + 1, ...post});		dialogVisible.value = false}const filteredPosts = computed(() => {		return posts.value.filter(post =>				post.title.toLowerCase().includes(findTitle.value.toLowerCase())		);});function filterPosts (searchTerm) {		findTitle.value = searchTerm;}function removePost (id) {		posts.value = posts.value.filter(post => post.id !== id)}function donePost (id) {		posts.value = posts.value.map(post => {				if (post.id === id) {						post.done = !post.done				}				return post		})}function showDialog() {		dialogVisible.value = true}</script ><style ></style >